import React, { useEffect, useState } from "react";

function ListTodo() {
  const [list, setList] = useState([]);
  const getList = async () => {
    try {
      const response = await fetch("http://localhost:5000/todos");
      const jsonData = await response.json();
      setList(jsonData);
    } catch (error) {
      console.log(error.message);
    }
  };

  useEffect(() => {
    getList();
  }, []);
  console.log(list[0]);
  return (
    <div>
      <table className="table-auto mx-auto w-1/2 border my-10">
        <thead className="bg-orange-400">
          <tr>
            <th className="font-semibold px-4 py-2">Item</th>
            <th className="font-semibold px-4 py-2">Description</th>
            <th className=" font-semibold px-4 py-2">Action</th>
          </tr>
        </thead>
        <tbody>
          {list.map((item) => (
            <tr>
              <td className="border px-4 py-2">{item.id}</td>
              <td className="border px-4 py-2">{item.description}</td>
              <td className="border px-4 py-2">action</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ListTodo;
